INSERT INTO movie_movie (title, release_year, synopsis, created_at, poster, trailer_url) VALUES
('Matrix', 1999, 'Thomas Anderson, um hacker conhecido como Neo, descobre que o mundo que conhece é uma simulação criada por máquinas e precisa escolher entre a verdade e a ilusão para liderar a resistência humana.', NOW(), 'movie_posters/matrix.jpg', 'https://youtu.be/vKQi3bBA1y8'),
('O Senhor dos Anéis: A Sociedade do Anel', 2001, 'Um jovem hobbit recebe a missão de destruir um anel mágico que pode trazer a ruína à Terra-média, formando uma sociedade com humanos, elfos, anões e outros hobbits para enfrentar as forças das trevas.', NOW(), 'movie_posters/lotr1.jpg', 'https://youtu.be/V75dMMIW2B4'),
('Clube da Luta', 1999, 'Um insatisfeito executivo forma um clube secreto de luta com um misterioso vendedor de sabão, mergulhando em uma jornada de autodescoberta, anarquia e destruição.', NOW(), 'movie_posters/fightclub.jpg', 'https://youtu.be/SUXWAEX2jlg'),
('A Origem', 2010, 'Um ladrão especializado em extrair segredos do subconsciente durante o sono é contratado para implantar uma ideia na mente de um herdeiro empresarial, embarcando em uma missão complexa que desafia a noção de realidade.', NOW(), 'movie_posters/inception.jpg', 'https://youtu.be/YoHD9XEInc0'),
('Titanic', 1997, 'Durante a fatídica viagem do Titanic, um artista pobre e uma jovem da alta sociedade vivem um romance proibido, enquanto o navio caminha para uma tragédia histórica.', NOW(), 'movie_posters/titanic.jpg', 'https://youtu.be/kVrqfYjkTdQ'),
('Jurassic Park', 1993, 'Cientistas usam engenharia genética para recriar dinossauros e montar um parque temático revolucionário, mas tudo sai do controle quando as criaturas escapam.', NOW(), 'movie_posters/jurassicpark.jpg', 'https://youtu.be/QWBKEmWWL38'),
('La La Land', 2016, 'Uma aspirante a atriz e um pianista de jazz tentam equilibrar amor e ambições pessoais em Los Angeles, onde o sucesso muitas vezes cobra um preço alto.', NOW(), 'movie_posters/lalaland.jpg', 'https://youtu.be/0pdqf4P9MB8'),
('Pulp Fiction', 1994, 'Várias histórias interconectadas de violência, redenção e humor ácido se desenrolam no submundo do crime em Los Angeles, com personagens excêntricos e situações inesperadas.', NOW(), 'movie_posters/pulpfiction.jpg', 'https://youtu.be/s7EdQ4FqbhY'),
('Django Livre', 2012, 'No sul dos EUA pré-Guerra Civil, um escravo liberto junta-se a um caçador de recompensas alemão para resgatar sua esposa de um cruel fazendeiro.', NOW(), 'movie_posters/django.jpg', 'https://youtu.be/0fUCuvNlOCg'),
('O Irlandês', 2019, 'Frank Sheeran, um veterano de guerra e assassino da máfia, reflete sobre sua vida de crimes e seu envolvimento com o misterioso desaparecimento do sindicalista Jimmy Hoffa.', NOW(), 'movie_posters/irishman.jpg', 'https://youtu.be/WHXxVmeGQUc'),
('Forrest Gump', 1994, 'Forrest Gump, um homem com QI abaixo da média, participa de diversos eventos históricos dos EUA, mostrando que gentileza e inocência podem mudar o mundo.', NOW(), 'movie_posters/forrestgump.jpg', 'https://youtu.be/bLvqoHBptjg'),
('Gladiador', 2000, 'Traído e forçado à escravidão, um general romano busca vingança como gladiador, desafiando o poder do império e a corrupção de seus governantes.', NOW(), 'movie_posters/gladiator.jpg', 'https://youtu.be/owK1qxDselE'),
('O Diabo Veste Prada', 2006, 'Uma jovem jornalista consegue emprego como assistente de uma poderosa editora de moda, enfrentando dilemas éticos e transformações pessoais em um ambiente altamente competitivo.', NOW(), 'movie_posters/devilwearsprada.jpg', 'https://youtu.be/vcCNxU1Oi5c'),
('Moonrise Kingdom', 2012, 'Dois jovens apaixonados fogem de casa em uma ilha isolada, desencadeando uma busca frenética e revelando as fragilidades dos adultos ao seu redor.', NOW(), 'movie_posters/moonrise.jpg', 'https://youtu.be/FAfR8omt-CY'),
('Jogos Vorazes', 2012, 'Katniss Everdeen se voluntaria para substituir sua irmã em um reality show mortal onde jovens lutam até a morte, desafiando o governo totalitário de Panem.', NOW(), 'movie_posters/hungergames.jpg', 'https://youtu.be/mfmrPu43DF8'),
('E.T. - O Extraterrestre', 1982, 'Um garoto solitário encontra um alienígena perdido e tenta ajudá-lo a voltar para seu planeta, formando uma amizade comovente que desafia as barreiras do espaço.', NOW(), 'movie_posters/et.jpg', 'https://youtu.be/qYAETtIIClk'),
('O Grande Hotel Budapeste', 2014, 'As aventuras de um concierge carismático e seu jovem protegido em meio a uma trama de herança, assassinato e guerra na Europa do século XX.', NOW(), 'movie_posters/budapest.jpg', 'https://youtu.be/FJfFZqTlWrQ'),
('Lost in Translation', 2003, 'Dois americanos solitários se conhecem em Tóquio e formam uma conexão profunda enquanto enfrentam crises pessoais e culturais.', NOW(), 'movie_posters/lostintranslation.jpg', 'https://youtu.be/F4XV3mLQNg4'),
('Taxi Driver', 1976, 'Um veterano mentalmente instável trabalha como taxista em Nova York e mergulha em uma espiral de violência e paranoia enquanto tenta "limpar" a cidade.', NOW(), 'movie_posters/taxidriver.jpg', 'https://youtu.be/UUxD4-dEzn0'),
('Avatar', 2009, 'Em um planeta alienígena, um ex-fuzileiro paraplégico é enviado para espionar os nativos, mas acaba se apaixonando pela cultura local e lidera uma rebelião contra os humanos.', NOW(), 'movie_posters/avatar.jpg', 'https://youtu.be/5PSNL1qE6VY'),
('Fences', 2016, 'Na década de 1950, um ex-jogador de beisebol negro luta para sustentar sua família, enfrentando arrependimentos e tensões raciais em um drama íntimo e poderoso.', NOW(), 'movie_posters/fences.jpg', 'https://youtu.be/4asqz0hd2Lo'),
('O Resgate do Soldado Ryan', 1998, 'Durante a invasão da Normandia, um grupo de soldados é enviado em uma missão perigosa para resgatar um único combatente cujos irmãos morreram na guerra.', NOW(), 'movie_posters/ryan.jpg', 'https://youtu.be/zwhP5b4tD6g'),
('O Silêncio dos Inocentes', 1991, 'Uma jovem agente do FBI busca a ajuda de um brilhante psiquiatra canibal para capturar um assassino em série, mergulhando em um jogo psicológico perigoso.', NOW(), 'movie_posters/silencio.jpg', 'https://youtu.be/W6Mm8Sbe__o'),
('Birdman', 2014, 'Um ator em decadência tenta recuperar sua fama montando uma peça da Broadway, lutando com sua identidade, ego e um alter ego super-heróico.', NOW(), 'movie_posters/birdman.jpg', 'https://youtu.be/uJfLoE6hanc'),
('A Rede Social', 2010, 'A criação do Facebook é contada através de disputas legais, traições e a ambição de um jovem gênio da computação que muda o mundo digital.', NOW(), 'movie_posters/socialnetwork.jpg', 'https://youtu.be/lB95KLmpLR4'),
('Her', 2013, 'Em um futuro próximo, um homem solitário se apaixona por um sistema operacional com inteligência artificial, questionando os limites do amor e da tecnologia.', NOW(), 'movie_posters/her.jpg', 'https://youtu.be/WzV6mXIOVl4');


